                cmmd.CommandText = "CREATE TABLE Australia ([Id] INT NOT NULL PRIMARY KEY IDENTITY, [Name] NVARCHAR(100), [Capital] NVARCHAR(100), [Population] NVARCHAR(100),  [Area] NVARCHAR(100),  [Mountain] NVARCHAR(100), [River] NVARCHAR(100),  [Currency] NVARCHAR(100), [Languages] NVARCHAR(400),  [Infos] NVARCHAR(4000))";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Australien','Canberra','25766605','7692024','Mont Kosciusko (2229 m)','Murray (2575 km)','Australischer Dollar','Englisch und die Sprachen der Aborigines','Im Jahr 1892 versuchte eine Gruppe von 200 mit der Regierung unzufriedenen Australiern in Paraguay eine Ablegerkolonie zu gründen, die - New Australia heißen sollte.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Neuseeland','Wellington','4917000','269652','Aoraki (Mount Cook, 3724 m)','Waikato River (425 km)','Neuseeland-Dollar','Englisch, Maori, neuseeländische Gebärdensprache','Maui’s Taktik und Motive waren oft schelmisch und vorsätzlich. Einige seiner Tricks und magischen Fähigkeiten hatten zufällige Folgen. Sein Verhalten führte zur Geburt von Polynesien und seiner Kulturen. Dies gelang ihm aus Versehen, indem er seine Brüder absichtlich herausforderte. Es gibt verschiedenste Sagen von unserem mythologischen Helden, z. B. die wie er die Sonne gefangen hat oder den Menschen das Feuer brachte.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Fidschi','Suva','889953','18333','Tomanivi (1324 Meter)','Rewa River (145 km)','Fidschi-Dollar (FJD)','Fidschi, Fidschi-Hindi und Englisch','Insgesamt leben mehr als 3.500 verschiedene Insektenarten auf allen Fidschi-Inseln.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Marshallinseln','Majuro','58791','181','','','US-Dollar','Marshallesisch und Englisch','Weihnachtsinsel, Teil von Kiribati, hat mehr Vogelarten als jede andere Insel der Welt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kiribati','South Tarawa','117606','811','','','Kiribati-Dollar und Australischer Dollar','Gilbertesisch und Englisch','Der wunderschöne Inselstaat Kiribati liegt im zentralen Pazifik und besteht aus 32 Atollen und einer erhöhten Koralleninsel. Die Hauptstadt South Tarawa liegt auf dem Atoll Tarawa und beherbergt etwa die Hälfte der Bevölkerung des Landes. Süd-Tarawa ist eine pulsierende Stadt mit einer lebendigen Kultur und einer einzigartigen Mischung aus traditioneller und moderner Architektur. Sie ist auch das politische und wirtschaftliche Zentrum des Landes und beherbergt das Büro des Präsidenten, das nationale Parlament und den Obersten Gerichtshof.\r\n')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Mikronesien','Palikir','500000','702','Mount Nahna (782 m)','Malem Fluss','US-Dollar','Koreanisch, Pohnpeanisch, Yapesisch, Chuukesisch, Ulithisch, Woleaianisch und Englisch','Es gibt nur sehr wenige veröffentlichte Arbeiten von Schriftstellern aus Mikronesien. Aber dafür hat die mikronesische Inselwelt einige fiktive Werke inspiriert. Nan Madol auf Pohnpei zum Beispiel dient als Vorlage für den 1918 veröffentlichten Roman „The Moon Pool“ von Abraham Merritt. Yap und seine Inseln spielen eine Rolle in „The Island of the Sequined Love Nun“ des Schrifftstellers Christopher Moore. Das Buch erschien 1997.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Nauru','Yaren','10084','21','Command Ridge (61 m)','','Australischer Dollar','Nauruisch und Englisch','In den 1980er Jahren war Nauru ein sagenhaft wohlhabendes Land zum Abbau von Phosphorit. Aber alle guten Dinge haben ein Ende – auch die Phosphorite sind aufgebraucht und mit ihnen die Liquidität. Während der Entwicklung wurden etwa 90 % der Wälder auf Nauru zerstört, und 80 % der gesamten Fläche der Insel verwandelten sich in eine leblose Wüste, die an die Oberfläche des Mondes erinnert.  Der höchste Punkt Naurus ist der Command Ridge mit nur 60 m Höhe über den Meeresspiegel.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Palau','Melekeok','18233','459','Ngermeskang River','Mount Ngerchelchuus (242 m)','US-Dollar','Palauisch, Englisch, Japanisch, Tobianisch, Angaur, Sonsorolesisch außerdem Japanisch und Angaur auf Angaur - Sonsorolesisch auf den Sonsorol-Inseln und Tobianisch auf Tobi','Eines der Naturwunder – Jellyfish Lake, wo sich diese Kreaturen unkontrolliert vermehrten, keine natürlichen Feinde hatten und die Fähigkeit zur Verteidigung verloren. Insgesamt leben mehr als zwei Millionen Quallen im See, das heißt durchschnittlich zwei pro Kubikmeter Wasser.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Papua-Neuguinea','Port Moresby','8776109','462840','Mount Wilhelm (4509 m)','Sepik (1200 km)','Kina','Tok Pisin - Hiri Motu - Englisch','Zu den Sprachen Papua-Neuguineas gehören je nach Quelle 700 bis 860 verschiedene Sprachen, damit ist Papua-Neuguinea der Staat mit der größten Vielzahl und Vielfalt an Sprachen in der Welt, Teil des Verbreitungsgebietes u. a. der austronesischen Sprachen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Salomonen','Honiara','669823','28370','Popomanaseu (2331 m)','Wairaha River','Salomonen-Dollar','Englisch, Umgangssprache Pidgin-Englisch','Marovo befindet sich hier – die größte Salzlagune der Welt. Übrigens ein wahnsinnig schöner Ort.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Samoa','Apia','202506','2842','Mt. Silisili auf der Insel Savaií (1858 m)','Mali´oli´o River','Tala','Samoanisch und Englisch','Viele Forscher stimmen darin überein, dass das traditionelle polynesische Tattoo, das heute auf der ganzen Welt sehr beliebt ist, zuerst hier aufgetaucht ist und aus Samoa bereits stammt Ausbreitung auf andere Inseln.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Tonga','Nuku´alofa','100209','747','','','Paʻanga (auch Tonga-Dollar)','Tongaisch, Englisch','Tonga ist ein Königreich und auch die einzige Erbmonarchie in Polynesien.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Tuvalu','Funafuti','11646','26','','','Australischer Dollar','Tuvaluisch, Englisch','Der höchste Punkt in Tuvalu liegt auf einer Höhe von 5 Metern über dem Meeresspiegel. Deshalb wird die Anhebung des Meeresspiegels das Land einfach zerstören. Bei einem Anstieg des Wasserspiegels soll die gesamte Bevölkerung nach Neuseeland evakuiert werden (Wissenswertes über Neuseeland).')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Australia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Vanuatu','Port Villa','299882','12190','Tabwemasana (1877 m)','Le Jourdain','Vatu','Französisch, Englisch und Bislama','Das beliebteste Getränk hier ist Kava, eine Tinktur aus einer der lokalen Früchte. Sie trinken es hier statt Bier.')";
