                #region Africa
                cmmd.CommandText = "CREATE TABLE Africa ([Id] INT NOT NULL PRIMARY KEY IDENTITY, [Name] NVARCHAR(100), [Capital] NVARCHAR(100), [Population] NVARCHAR(100),  [Area] NVARCHAR(100),  [Mountain] NVARCHAR(100), [River] NVARCHAR(100),  [Currency] NVARCHAR(100), [Languages] NVARCHAR(400),  [Infos] NVARCHAR(4000))";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Ägypten','Kairo','100388073','1001450','Dschabal Katrina(2.637 m)','Nil(6.671 km)','Ägyptisches Pfund','Arabisch','Horus ist der Gott der Könige, des Himmels des Lichts und der Welt. Im Laufe der Jahrtausende wurde er zu einem der Hauptgötter Ägyptens.Es gibt sehr viele Mythen und aber auch Verfilmungen über diesen Mythologischen Charakter.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Algerien','Algier','43053054','2381741','Tahat(2918 m)','Chelif(725 km)','Algerischer Dinar','Arabisch und Tamazight','Algerien gehört zu den reicheren Ländern Afrikas.Das hängt vor allem mit dem Erdöl und dem Erdgas zusammen.Auch die Bodenschätze bringen viel Geld ein.Sie werden auch im Land verarbeitet, zum Beispiel zu Eisen und Stahl.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Angola','Luanda','31825295','1246700','Serra do Moco(2.610 m)','Cubango(ca. 1.700 km)','Kwanza','Portugiesisch','Interessant ist dass auch Cabinda zu Angola gehört. Cabinda ist ein Exklave von Angola. Dies bedeutet dass es keine direkte Verbindung mit dem Rest des Landes hat.Die Exklave grenzt an die Republik Kongo und an die Dem.Rep.Kongo. ')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Äquatorialguinea','Malabo','1355986','28051','Pico Basile(3011 m)','Mbini(auch: Benito, 300 km)','CFA - Franc(XAF)','Spanisch, Französisch, Portugiesisch','Der Goliathfrosch ist mit ca. 30 cm Länge so lange wie ein Schullineal.Der größte Frosch der Welt wiegt sagenumwobene 3 kg.Er lebt im Nationalüark Monte Alen und soll laut den Anwohnern sehr gut schmecken. (Ravensburger Alle Länder dieser Welt S. 115)')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Äthiopien','Addis Abeba','112078730','1133380','Ras Daschän(4550 m)','Omo(circa 1000 km)','Birr','Amharisch','Das Leben der heutigen Menschen begann in Äthiopien.Hier wurden die ältesten, 200.000 Jahre alten, menschlichen Fossilien gefunden. Die Knochen von Lucy, eine Vertreterin der frühesten, menschenähnlichen(hominiden) Art, sind sogar 3,2 Millionen Jahre alt.In Äthiopien wurden auch 2,5 Millionen Jahre alte Steinwerkzeuge gefunden(S. 124 Ravensburger - Alle Länder dieser Welt)')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Benin','Porto Novo','11801151','112622','Mont Sokbaro(658 m)','Ouémé(510 km)','CFA - Franc(XAF)','Französisch','Das Dorf Ganvie liegt im Nokoué-See.Die cleveren Dorfbewohner schützten sich mit ihren Häusern auf Stelzen vor den Dahomey. Dieser Stamm glaubte an einen Wasserdämon, der den See bewacht.Um ihre verstobenen Könige zu besänftigen, opferten die blutrünstigen Dohomey Menschen, die die Geister Könige im Leben nach dem Tod bedienen mussten!(S. 112 Ravensburger - Alle Länder dieser Welt')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Botswana','Gaborone','2346179','581730','Monalanong Hill(1494 m)','Limpopo(Gesamtlänge 1750 km)','Pula','Setswana und Englisch','In Tsodilo wurdne 4.500 Felsmalerein gefunden. Dieser kleine Teil der Kalahari trägt deshalb den Spitznamen - Louvre der Wüste  - einer Anspielung an den französischen Louvre.S. 135 Ravensburger - Alle Länder dieser Welt')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Burkina Faso','Ouagadougou','20321378','274000','Tena Kourou(749 m)','Mohoun(Schwarzer Volta, in Burkina Faso ca. 700 km)','CFA - Franc(XAF)','Französisch','Das Land ist bekannt für seine reiche Kultur, seine Musik, seine Masken und seine traditionellen Feste, die die Vielfalt der ethnischen Gruppen widerspiegeln.Burkina Faso ist aber auch eines der ärmsten Länder der Welt, das mit Dürre, Armut, Korruption und politischer Instabilität zu kämpfen hat.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Burundi','Gitega','11530580','27834','Mont Heha(2684 m)','Ruvubu(Gesamtlänge 380 km)','Burundi - Franc','Kirundi und Französisch','Der Tanganjika liegt mitten in Afrika und zählt zu den größten Seen der Erde.Doch in Europa ist er weitgehend unbekannt.An seinen Ufern liegen Nationalparks und unerschlossene Wildnis, aber die stetig wachsende Bevölkerung der Anrainerstaaten macht dem natürlichen Fischreichtum des Tanganjika mehr und mehr zu schaffen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Elfenbeinküste','Yamoussoukro','25716544','322461','Mont Nimba(1752 m)','Comoé(1160 km)','CFA - Franc(XAF)','Französisch','Als Elfenbein bezeichnet man im engeren Sinne die Substanz der Stoßzähne von Elefanten(und ausgestorbener Mammute: fossiles Elfenbein).Elefanten sind heute die „Hauptquelle“ von Elfenbein. Sie werden gnadenlos gejagt und getötet, um an ihre Stoßzähne zu gelangen.Im weiteren Sinne wird unter Elfenbein auch das Zahnbein der Stoß - und Eckzähne verschiedener Säugetiere verstanden(Walross, Pottwal, Narwal, Flusspferd).')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Eritrea','Asmara','6333135','121100','Soira(3018 m)','Barka(500 km)','Nakfa','Tigrinya und Arabisch','Früher gehörte dieses Land einmal zu seinem Nachbarn Äthipien.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Dschibuti','Dschibuti','973560','23200','Mousa Alli(2028 m)','Essalou(?)','Dschibuti - Franc','Arabisch und Französisch','In Dschibuti wird Salz aus dem Assalsee gebrochen und mit Kamelen transportiert – wie vor Jahrhunderten.Kaum ein Tourist verirrt sich in das Land. Dabei locken schöne Strände, spektakuläre Landschaften und im Meer Korallen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Gabun','Libreville','2172579','267667','Mont Iboundji oder Mont Bengoué(ca. 1000 m)','Ogooué(1200 km)','CFA - Franc(XAF)','Französisch','Die Gabunviper wird auf Grund ihrer Trägheit und Bissfaulheit von den Einheimischen oft sehr sorglos behandelt. Mit allerdings dramatischen und lebensgefährlichen Folgen bei einem Biss.Aufgrund ihrer schönen Färbung wird sie auch als - Schmetterlingsviper - bezeichnet.Die Gabunviper gehört zur Gattung der Puffottern.Sie besitzt von allen Arten der Gattung der Puffottern das wirksamste und gefährlichste Gift.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Gambia','Banjul','2347706','11295','Namenlose Erhebung im Osten des Landes(53 m)','Gambia(1120 km)','Dalasi','Englisch','einen Namen hat Gambia vom gleichnamigen Fluss, der schon immer die Lebensader des Landes war. Der Name „The Gambia“ ist ebenfalls geläufig und wird euch sicher immer wieder begegnen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Ghana','Accra','30792608','239537','Mount Afadjato(885 m)','Volta(1500 km)','Cedi','Englisch','Anansi die Spinne - Diese mythologische Figur gilt zwar als Trickster, jedoch als guter, denn er gab den Menschen dass ganze Wissen und behilet es nicht für sich selbst. Aber damit er das ganze Wissen des Planeten bekommen konnte stellte ihm der Himmels Gott 3 sehr schwere Aufgaben...')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Guinea','Conakry','12771246','245857','Mont Richard-Molard, auch Mount Nimba genannt(1752 m)','Tinkisso(570 km)','Guinea - Franc(Franc Guinéen)','Französisch','Guinea erlangte 1958 die Unabhängigkeit von Frankreich.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Guinea - Bissau','Bissau','1920922','36125','Madina do Boé(262 m)','Geba(545 km)','CFA - Franc(XAF)','Portugiesisch','Die Republik Guinea - Bissau, offiziell als Republica da Guine - Bissau bekannt, ist ein kleiner westafrikanischer Staat an der Atlantikküste. Die Hauptstadt und größte Stadt ist Bissau, eine pulsierende Hafenstadt mit über 500.000 Einwohnern.Bissau ist das politische, wirtschaftliche und kulturelle Zentrum des Landes und beherbergt eine Reihe von wichtigen Regierungsgebäuden, Universitäten und anderen Einrichtungen.Die Stadt ist auch ein beliebtes Touristenziel und bietet eine Vielzahl von Attraktionen wie die koloniale Architektur, schöne Strände und ein pulsierendes Nachtleben.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kamerun','Yaoundé','25876380','475442','Kamerunberg(auch Fako genannt, 4095 m)','Sanaga(918 km)','CFA - Franc(XAF)','Französisch und Englisch','In Kamerun, an den Hängen des gleichnamigen Vulkans, dem «nassen» Ort Afrikas und dem zweitniederschlagsreichsten Ort der Welt – jährlich erreicht die Niederschlagsmenge 9655 mm(interessante Fakten über Vulkane).')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kap Verde','Praia','483628','4036','Pico do Fogo(2829 m)','Ribeira de Torre(?)','Kap - Verde - Escudo','Portugiesisch','Die berühmteste Bürgerin des Landes, die hier von allen geliebt wird – die weltberühmte Sängerin Cesaria Evoro, die für Mehrere Jahrzehnte lang hat sie das gesamte Bildungssystem der Kapverden aus eigener Tasche bezahlt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kenia','Nairobi','52573973','580367','Batian(5199 m)','Tana(circa 800 km)','Kenia - Schilling','Swahili und Englisch','In Kenia leben mehr als 40 ethnische Gruppen, die alle ihre eigenen Sprachen sprechen.Wirtschaftlich geht es Kenia im Vergleich zu anderen Ländern der südlichen Sahara sehr viel besser.Die meisten Menschen arbeiten in der Landwirtschaft, obwohl nur ein Fünftel des Landes überhaupt für diese genutzt werden kann.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Komoren','Moroni','850886','1862','Karthala(2361 m)','','Komoren - Franc','Komorisch, Französisch und Arabisch','Die Komoren sind ein atemberaubender Archipel von Vulkaninseln, deren Inneres von niedrigen Hügeln bis zu steilen Bergen reicht.Der höchste Gipfel ist der Berg Karthala, ein aktiver Vulkan mit einer Höhe von 2.361 Metern über dem Meeresspiegel. Die Inseln beherbergen auch zahlreiche Kraterseen, üppige Regenwälder und wunderschöne Strände. Die Inseln sind ein Paradies für Naturliebhaber und bieten eine einzigartige Kombination aus atemberaubenden Landschaften und einer vielfältigen Tierwelt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Demokratische Republik Kongo','Kinshasa','86790567','2344885','Margherita Peak(5109 m)','Kongo(4374 km)','Kongo - Franc','Französisch','Eigentlich ist die Demokratische Republik Kongo(DR Kongo) ein reiches Land: Sie verfügt über wertvolle Rohstoffe, große Süßwasserreserven und riesige tropische Regenwälder. Doch viele Jahrzehnte ausbeuterischer Kolonialherrschaft und anschließender Diktatur sowie darauf folgende Kriege haben dazu geführt, dass die DR Kongo heute bitterarm ist.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Republik Kongo','Brazzaville','5380508','342000','Mount Nabemba(1020 m)','Kouilou(700 km)','CFA - Franc(XAF)','Französisch','Etwa 40 % der Kongolesen gehören der katholischen Kirche an, circa 10 % sind Protestanten.Die starke Verbreitung des Christentums in der Republik Kongo ist vorrangig auf die europäische Kolonialherrschaft(Frankreich) und auf die beharrliche Missionsarbeit zurückzuführen. Rund 48 % der Bürger sind Anhänger traditioneller Naturreligionen und bilden damit die zweite große religiöse Gemeinschaft im Staat.Eine Minderheit von knapp 2 % der kongolesischen Bevölkerung bekennt sich zum Islam. Daher stammt auch die berühmte Tempelmoschee in Brazzaville')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Lesotho','Maseru','2125268','30355','Thabana Ntlenyana(3482 m)','Oranje(2160 km)','Loti','Sesotho und Englisch','Lesotho ist ein kleines Königreich in Afrika, das komplett von Südafrika umschlossen ist. Es ist eine Enklave und ein Binnenstaat, der an kein Meer grenzt.Lesotho liegt überwiegend über 1000 Höhenmetern und hat eine beeindruckende Landschaft14. Die Hauptstadt ist Maseru. Die meisten Einwohner gehören dem Volk der Basotho an')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Liberia','Monrovia','4937374','97754','Mount Wuteve(1440 m)','Cavally(515 km)','Liberianischer Dollar','Englisch','Die Gründer von Liberia kauften 13.000 km² Land von lokalen Stämmen für Waren, die 50 US - Dollar entsprachen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Libyen','Tripolis','6777452','1775500','Bikku Bitti(2267 m)','','Libyscher Dinar','Arabisch','Shakshuka ist ein sehr beliebtes Frühstück. Es ist eine bunte Mischung aus gebratenen Eiern, Tomaten, Zwiebeln, Chilischoten und Gewürzen. Meist wird Brot dazu serviert, um die leckere Soße aufzuwischen.S. 93 Ravensburger - Alle Länder dieser Welt')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Madagaskar','Antananarivo','26969307','587295','Maromokotro(2876 m)','Mangoky(564 km)','Ariary','Malagasy und Französisch','Die Tierwelt von Madagaskar umfasst über 250.000 Tier - und Pflanzenarten.Einige der bekanntesten Arten sind die Lemuren, die nur auf Madagaskar vorkommen.Weitere Tiere auf Madagaskar sind: Bunte Chamäleons, Geckos, Schildkröten, Schlangen und Frösche. Eine schillernde Vogelwelt: Papageiensorten, Madagaskar - Fischadler und Madagaskar-Falke.Säugetiere wie Fingertiere, Indris, Kattas, Mausmakis, Sifakas, Varis, Fossa (Schleichkatze), Tentreks (Borstenigel) und Fledertiere sowie Nagetiere.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Malawi','Lilongwe','18628747','118480','Sapitwa(3002 m)','Shire(402 km)','Kwacha','Englisch und Chichewa','Der Malawisee oder Njassasee(nach Nyasa in Tansania, Niassa in Mosambik; von Yao nyasa „See“) in Ostafrika ist der neuntgrößte See der Erde.Sein Abfluss ist der Shire am südlichen Ende. Die Anrainerstaaten des Sees sind Tansania, Malawi und Mosambik, wobei Malawi die längste Uferlinie hat und fast das komplette Westufer umfasst.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Mali','Bamako','19658031','1240192','Hombori Tondo(1153 m)','Niger(4184 km)','CFA - Franc(XAF)','Französisch','Mansa Musa -Mächtig sind die Reiche Westafrikas, doch keines übertrifft Mali.Dessen muslimischer Herrscher Mansa Musa gebietet um 1325 über Millionen Menschen – und riesige Bodenschätze.So wird er zum wahrscheinlich reichsten Menschen aller Zeiten.https://www.geo.de/wissen/22404-rtkl-der-goldkoenig-wie-mansa-musa-zum-wohl-reichsten-mann-aller-zeiten-wurde')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Marokko','Rabat','36910560','458730','Jbel Toubkal(4167 m)','Oum er-Rbia(555 km)','Dirham','Arabisch und Tamazight','Das zauberhafte Fès wird als die -am besten erhaltene mittelalterliche Stadt in der arabischen Welt - bezeichnet.Die Männer tragen den Fes, eine traditionelle rote Filzkappe mit schwarzer Quaste. Er wurde unter osmanischer Herrschaft 1826 von Sultan Mahmud II. eingeführt.S. 90 Ravensburger - Alle Länder dieser Welt')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Mauretanien','Nouakchott','4525696','1030700','Kediet Ijill(915 m)','Senegal(813 km)','Ouguiya','Arabisch','In Sachen Plastikmüll ist Mauretanien Deutschland einen großen Schritt voraus: Seit 2013 ist es verboten, Weichplastik herzustellen, zu verteilen oder zu gebrauchen. Plastiktüten und vieles mehr wurden damit im ganzen Land abgeschafft. Übrigens dürfen auch Reisende keine Plastiktüten mit ins Land bringen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Mauritius','Port Louis','1265711','2040','Piton de la Petite Rivière Noire(828 m)','Grande Rivière du Sud-Est(34 km)','Mauritius - Rupie','Englisch und Französisch','Die Blaue Mauritius ist weder die seltenste noch die wertvollste Briefmarke der Welt.Sie ist lediglich nur “eine” der wertvollsten und seltenen Briefmarken.Ihrer Popularität tut dies jedoch keinen Abbruch.Leidenschaftliche Sammler sehnen sich noch immer danach, das wertvolle Exemplar zu besitzen.Allerdings gibt es sie in der ersten Version nur noch in Museen zu bewundern.Die Rede ist von der Blauen Mauritius, der berühmtesten Briefmarke unseres Planeten.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Mosambik','Maputo','30366036','801590','Monte Binga(2436 m)','Sambesi(2574 km)','Metical ','Portugiesisch','Graça Machel war bis 1986 die Frau des Präsidenten von Mosambik, und 12 Jahre nach seinem Tod heiratete sie das Oberhaupt von Südafrika, Nelson Mandela.So wurde sie die einzige Frau in der Geschichte, die die First Lady zweier verschiedener Staaten war.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Namibia','Windhoek(Windhuk)','2494530','824290','Königstein(2573 m)','Sambesi(2574 km)','Namibia - Dollar','Englisch und mehrere Nationalsprachen, darunter Deutsch und Afrikaans','Im Hinblick auf die folgenden Informationen, ist es wichtig, dass Du einen kurzen Einblick in die Geschickte Namibias erhältst. Zwar ist Namibia erst seit 1990 unabhängig und somit ein relativ junger Staat.Doch insbesondere seine vorherige Geschichte hat das Land stark geprägt. Die deutsche Kolonialzeit, die ab 1884 begann, brachte viele Städte mit deutscher Architektonik hervor.Ab 1920 war Namibia südafrikanische Kolonie, was Grund für die vielen Gemeinsamkeiten zwischen den beiden Ländern ist.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Niger','Niamey','23310715','1267000','Idoukal - n - Taghès(2022 m)','Niger(4184 km)','CFA - Franc(XAF)','Französisch','Der Niger, einer der größten Flüsse Afrikas, ist für Hunderte Millionen Menschen von lebenswichtiger Bedeutung.Die Bewohner der Länder, durch die der Niger fließt, nennen ihn anders – Cuarra, Joliba, Izo und ein gutes Dutzend anderer Namen.Sie alle haben eines gemeinsam – alle bedeuten in der Übersetzung entweder einfach „Fluss“ oder „großer Fluss“. Im Nigerdelta gibt es reiche Ölfelder. Es produziert mehr als 2 Millionen Barrel “schwarzes Gold” pro Jahr.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Nigeria','Abuja','200963599','923768','Chappal Waddi(2419 m)','Benue(1400 km)','Naira','Englisch','Nigeria ist das bevölkerungsreichste Land Afrikas mit circa 167 Millionen Einwohner. Es gibt rund 400 unterschiedliche Volksgruppen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Ruanda','Kigali','12626950','26338','Karisimbi(4507 m)','Nyabarongo(300 km)','Ruanda - Franc','Kinyarwanda, Französisch, Englisch und Swahili','Zu den anderen Ruandern gehören Vertreter des Volkes der Tutsi. Sie sind die größten Menschen auf dem Planeten, sogar höher als die Montenegriner. Natürlich gibt es hier kleine Menschen, aber die Durchschnittsgröße hier ist wirklich beeindruckend.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Demokratische Arabische Republik Sahara(Westsahara)','El Aaiún','570866','266000','Aousserd(701 m)','','Saharaui - Peseta','Arabisch und Spanisch','Im Jahr 1346 entdeckten die Portugiesen eine Bucht, die sie fälschlicherweise mit dem südlicheren Río de Oro, möglicherweise dem Fluss Sénégal, identifizierten. Die Europäer studierten diese Küstenregion bis zur Ankunft schottischer und spanischer Kaufleute Mitte des 19.Jahrhunderts wenig.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Sambia','Lusaka','17861030','752614','Mafinga(2339 m)','Sambesi(2574 km)','Kwacha','Englisch und sieben regionale Sprachen: Bemba, Nyanja, Lozi, Tonga, Lunda, Kaonde, Luvale','Die Victoriafälle sind ein breiter Wasserfall des Sambesi zwischen den Grenzstädten Victoria Falls in Simbabwe und Livingstone in Sambia.Seit 1989 gehören die Fälle zum Weltnaturerbe der UNESCO.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('São Tomé und Príncipe','São Tomé','215056','1001','Pico de São Tomé(2024 m)','','Dobra','Portugiesisch und mehrere Kreolsprachen','São Tomé und Príncipe wurden 1471 von portugiesischen Seefahrern entdeckt.Das Land galt während der Kolonialzeit als größter Kakaoproduzent der Welt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Senegal','Dakar','16296364','196722','Sambaya(395 m','Senegal(813 km)','CFA - Franc(XFA)','Französisch','Der Lac Retba in der Nähe von Dakar leuchtet je nach Jahreszeit in verschiedenen Rosatönen.Das kommt von dem enormen Salzgehalt des Wassers – bis zu zehnmal so hoch wie der des Meeres.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Seychellen','Victoria','97625','455','Morne Seychellois(905 m)','','Seychellen - Rupie','Seychellenkreol, Englisch und Französisch','Die Seychellen sind ein Inselparadies im Indischen Ozean, das aus 115 Inseln und Atollen besteht.Sie liegen im Nordosten Afrikas, etwa 1.500 Kilometer östlich des afrikanischen Kontinents.Die Seychellen sind ein Bijou des Indischen Ozeans und bieten ein unvergleichliches Erlebnis.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Sierra Leone','Freetown','7813215','71740','Bintumani(1948 m hoch)','Rokel(386 km)','Leone','Englisch','Während der gesamten Beobachtungsgeschichte betrug das Temperaturminimum in Sierra Leone + 19 ° C.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Simbabwe','Harare','14645468','390757','Inyangani(2592 m)','Sambesi(2574 km)','bis 2009 Simbabwe - Dollar, heute US - Dollar, Rand, Renminbi, Euro','16 Amtssprachen, vor allem Englisch, Shona und Ndebele','Nelson Mandela war von 1994 bis 1999 der erste schwarze Präsident Südafrikas.Er kämpfte viele Jahrzehnte gegen die Apartheid und bereitete den versöhnlichen Übergang zu einem demokratischen Staatswesen vor.Nelson Rolihlahla Mandela wurde am 18.Juli 1918 in Mvezo geboren und starb am 5.Dezember 2013 in Johannesburg.In Südafrika ist er unter seinem traditionellen Clannamen Madiba bekannt, wird oft auch als Tata bezeichnet.Tata bedeutet in der Sprache der Xhosa Vater.Mandela erhielt 1993 den Friedensnobelpreis.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Somalia','Mogadischu','15442905','637657','Shimbiris(2460 m)','Jubba(1658 km)','Somalia - Schilling','Somali und Arabisch','Somalia ist Weltrekordhalter beim Export von Myrrhe und Weihrauch.Der Hauptwert für Somalis sind Kamele.Wer ein Dutzend dieser Tiere sowie Schaf - und Ziegenherden besitzt, gilt als wohlhabend.Die wirklich Reichen sind Menschen, die es geschafft haben, 100 bis 1000 Kamele zu erwerben.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Südafrika','Pretoria(Tshwane)','58558270','1219912','Mafadi(3450 m)','Oranje(1860 km)','Rand','Afrikaans und Englisch sowie Ndebele, Northern Sotho, Sesotho, Siswati, Tsonga, Tswana, Venda, Xhosa, Zulu','Nelson Mandela war von 1994 bis 1999 der erste schwarze Präsident Südafrikas.Er kämpfte viele Jahrzehnte gegen die Apartheid und bereitete den versöhnlichen Übergang zu einem demokratischen Staatswesen vor.Nelson Rolihlahla Mandela wurde am 18.Juli 1918 in Mvezo geboren und starb am 5.Dezember 2013 in Johannesburg.In Südafrika ist er unter seinem traditionellen Clannamen Madiba bekannt, wird oft auch als Tata bezeichnet.Tata bedeutet in der Sprache der Xhosa Vater.Mandela erhielt 1993 den Friedensnobelpreis.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Südsudan','Juba','11062113','644329','Kinyeti(3187 m)','Weißer Nil(3762 km)','Südsudanesisches Pfund','Englisch sowie alle einheimischen Sprachen','Der Südsudan wurde am 9.Juli 2011 nach einem Referendum mit 98, 83 % der Stimmen zu einem unabhängigen Staat.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Sudan','Khartum','42813238','1861484','Jebel Marra(3042 m)','Nil(6852 km)','Sudanesisches Pfund','Englisch und Arabisch','Zwischen dem Sudan und Ägypten liegt ein Gebiet von über 2.000 Quadratkilometern, das niemandem gehört.Die Briten, die die Grenze zwischen den beiden Ländern legten, gaben diese Gebiete an Ägypten, aber die ägyptischen Behörden gaben sie bald auf und die Führung des Sudan wollte das Land nicht zurückerobern.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Eswatini','Mbabane','1093238','17363','Emlembe(1862 m)','Lusutfu(auch Great Usuto, Gesamtlänge 300 km)','Lilangeni und Südafrikanischer Rand','Siswati und Englisch','Das Königreich Eswatini hieß früher Swasiland und ist vielen auch noch unter diesem Namen bekannt.Es ist der zweitkleinste Staat in ganz Afrika.Nur Gambia ist kleiner.Eswatini liegt im Süden von Afrika und grenzt an Südafrika und Mosambik.Hier herrscht noch wie zu alten Zeiten ein König über sein Volk, die Swasi.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Tansania','Dodoma','58005463','945087','Kibo(5895 m)','Rufiji(600 km)','Tansania - Shilling','Swahili und Englisch','Das Monument de Le Independance wurde am 27. April 1960 als Hommage an Togos Unabhängigkeit von Frankreich erbaut. Die Struktur besteht aus einer menschlichen Silhouette, die in dieses Gebäude gehauen ist und von Promenaden, Palmen, gepflegten Rasenflächen, Springbrunnen und einem schwarzen Goldeisen umgeben ist Zaun.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Togo','Lomé','8082366','56785','Mont Agou(986 m)','Mono(467 km)','CFA - Franc(XFA)','Französisch, Kabiyé, Ewe','Das Monument de Le Independance wurde am 27. April 1960 als Hommage an Togos Unabhängigkeit von Frankreich erbaut. Die Struktur besteht aus einer menschlichen Silhouette - die in dieses Gebäude gehauen ist und von Promenaden, Palmen, gepflegten Rasenflächen, Springbrunnen und einem schwarzen Goldeisen umgeben ist Zaun.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Tschad','N Djamena','15946876','1284000','Emi Koussi (3445 m)','Schari (1400 km)','CFA-Franc (XFA)','Französisch, Arabisch','N Djamena ist die Hauptstadt und größte Stadt des Tschads.Es wurde am 29.Mai 1900 vom französischen Kommandanten Emile Gentil als Fort - Lamy gegründet und nach Amedee-Francois Lamy benannt - einem Offizier - der einige Tage zuvor in der Schlacht von Kousseri ums Leben gekommen war.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Tunesien','Tunis','11694719','163610','Djebel Chambi(1544 m)','Medjerda(450 km)','Tunesischer Dinar','Arabisch','Bei Tunis findest du die Ruinen der Stadt Karthago - von den Römern gefürchtet und beneidet.Vor etwa 2.200 Jahren drang der legendäre Hannibal, ein General der Karthager, bis nach Italien vor.In seinem Heer gab es sogar Elefanten.Nach drei Kriegen, die zusammen 118 Jahre dauerten, eroberten die Römer schließlich Karthago und zerstörten es.Julius Cäsar kieß es erst hunder Jahre später wieder aufbauen. S. 92 Ravensburger - Alle Länder dieser Welt. ')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Uganda','Kampala','44269594','241040','Margherita Peak im Mount Stanley(5109 m)','Viktoria - Nil(Teil vom Nil)','Uganda - Schilling','Swahili und Englisch','Die Ugander lieben Fleischeintöpfe, aber das “Fleisch” ist nicht nur das Fleisch als solches, sondern auch Leber, Magen, Innereien, Zunge und andere Teile des Tieres. Ein ugandischer Koch lässt nichts verkommen!')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Africa (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Zentralafrikanische Republik','Bangui','4745185','622436','Mont Ngaoui(1420 m)','Sangha(Nebenfluss vom Kongo)','CFA - Franc(XFA)','Sango, Französisch','Der Diktator Bokassa, der einst in der Zentralafrikanischen Republik regierte, krönte sich selbst zum Kaiser und benannte das Land in Zentralafrikanisches Reich um. Der Kaiser regierte zwar nicht lange – drei Jahre später wurde er gestürzt.')";
                cmmd.ExecuteNonQuery();
                #endregion

                #region Asia
                cmmd.CommandText = "CREATE TABLE Asia ([Id] INT NOT NULL PRIMARY KEY IDENTITY, [Name] NVARCHAR(100), [Capital] NVARCHAR(100), [Population] NVARCHAR(100),  [Area] NVARCHAR(100),  [Mountain] NVARCHAR(100), [River] NVARCHAR(100),  [Currency] NVARCHAR(100), [Languages] NVARCHAR(400),  [Infos] NVARCHAR(4000))";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Afghanistan','Kabul','38041754','652864','Nowshak(7485 m)','Amu Darya(2500 km)','Afghani','Paschto und Persisch','Afghanistan versank nach der Besetzung durch die Sowjetunion (1979-1989) im Bürgerkrieg und wurde eine Brutstätte des internationalen Terrorismus. Es beherbergte Osama bin Laden, der von dort aus die Anschläge des 11. September 2001 plante. Bald darauf wurde die radikalislamische Taliban-Regierung von der westlichen Militärallianz und der afghanischen Nordallianz gestürzt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Armenien','Jerewan','2957731','29743','Aragaz(4090 m)','Aras(914 km)','Dram','Armenisch','Während der Herrschaft von König Tigranes war Armenien das mächtigste Land der Welt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Aserbaidschan','Baku','10023318','86600','Bazardüzü(4466 m)','Kura(1507 km)','Aserbaidschan - Manat','Aserbaidschanisch(Aseri)','Das archäologische Reservat Gobustan, berühmt für seine alten Felsmalereien und in Aserbaidschan gelegen, ist als ein anerkannt UNESCO-Weltkulturerbe.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Bahrain','Manama','1501635','750','Jabal al Bukhan(135 m)','','Bahrain - Dinar','Arabisch','Eine der lokalen Attraktionen ist ein uralter 400 Jahre alter Baum, der allein in der Wüste am höchsten Punkt des Landes wächst.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Bangladesch','Dhaka','167310838','147570','Mowdok Mual(1063 m)','Brahmaputra(2900 km)','Taka','Bengalisch und Englisch','Bengalischer Tiger: Das Nationaltier Bangladeschs und Indiens gilt als stark gefährdet. Mehrere Hundert Tiere mit dem charakteristischen rot-goldenen Fell und weißen Bauch leben noch in den Sundabarns. Besonders die männlichen Tiger beeindrucken mit einer Länge von bis zu 3 Metern und einem Gewicht bis zu 300 Kilogramm.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Bhutan','Thimphu','727145','38394','Gangkhar(7570 m)','Torsa(358 km)','Ngultrum und Indische Rupie','Dzongkha','Noch bis in das Jahr 1999 war es in Bhutan  verboten einen Fernseher zu besitzen. Damit ist Bhutan das letzte Land der Welt, das den Fernseher eingeführt hat. Heute beklagen sich viele Bhutaner darüber, dass der Fernseher viele negative Effekte auf die Bevölkerung habe. Er führe zu einem Aussterben von Traditionen. Während die Leute früher an den Abenden oft lange zusammengesessen hätten und getrunken und gesungen haben, säßen heute fast alle jeden Tag um diese Zeit vor dem Fernseher.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Brunei','Bandar Seri Begawan','433285','5765','Bukit Pagon(1850 m)','Belait(209 km)','Brunei - Dollar','Malaiisch, aber auch Chinesisch und Englisch','Der Sultan von Brunei verkörpert sowohl weltliche als auch religiöse Macht. Fast die gesamte Regierung besteht aus Verwandten des Sultans, und die einzige politische Partei unterstützt das bestehende politische System.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Georgien','Tiflis(Tbilissi)','3700000','69700','Schchara(5200 m)','Kura(1364 km)','Lari','Georgisch','Trotz Landwirtschaft ist fast die Hälfte des Landes mit Wald bedeckt. Bis zu 600 Metern Höhe wachsen überwiegend Eichen und Buchen, in höheren Lagen dominieren Nadelwälder und alpine Wiesen. In den Nationalparks wie dem Bordschomi-Charagauli-Nationalpark – einem der größten zusammenhängenden Naturschutzgebiete in Asien – leben Braunbären, Wölfe und Luchse.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Indien','Neu Delhi','1420000','3287469','Kangchenjunga(8598 m)','Indus(3180 km)','Indische Rupie','Hindi, aber auch Englisch und 17 weitere offiziell anerkannte Sprachen','Shakuntala Devi hält den Weltrekord im Kopfrechnen von langen Zahlen. Im Jahr 1980 wählte ein Computer per Zufall die zwei 13-stelligen Zahlen 7.686.369.774.870 und 2.465.099.745.779 aus, die Shakatunala dann multiplizieren sollte. Sie benötigte 28 Sekunden für die richtige Antwort: 18.947.668.117.995.426.462.773.730. Zudem gibt es hier das Taj Mahal.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Indonesien','Jakarta','270203917','2020000','Puncak Jaya(5030 m)','Kapuas(1150 km)','Rupiah','Indonesisch','In Indonesien gibt es eine kleine Tierart, den sogenannten Musang, welcher Kaffeekirschen frisst und diese wieder ausscheidet. Aus dieser Musang-Kacke wird dann Kaffee produziert, welcher zu den teuersten Kaffeesorten der Welt gehört.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Irak','Bagdad','39309783','438317','Cheekha Dar(3611 m)','Tigris(1900 km)','Irakischer Dinar','Arabisch und Kurdisch','Der Turm zu Babel, von dem eines der berühmtesten biblischen Gleichnisse erzählt, befand sich angeblich in den Ländern des modernen Irak. Gott hat ehrgeizige Menschen daran gehindert, einen gigantischen Wolkenkratzer bis in den Himmel zu bauen, indem er ihre eigenen Sprachen für verschiedene Völker geschaffen hat – die bauer verstanden sich nicht mehr und ihre arbeit war zum scheitern verurteilt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Iran','Teheran','82913906','1648195','Damāwand(5604 m)','Karun(720 km)','Iranischer Rial','Persisch','Es ist kein Geheimnis, dass der Iran im Allgemeinen für seine wunderschönen Teppiche und Kalligraphie bekannt ist. Wusstest du jedoch, dass das Land den längsten Teppich der Welt produziert hatte? Das stimmt. Die Iran Carpet Company fertigte einen Teppich an, von dem angenommen wird, dass er sich über 5574 qm erstreckt.Er wurde 2007 in der Moschee von Abu Dhabi enthüllt. Ursprünglich in 9 Teilen geschaffen, wurde der Teppich letztendlich im Inneren der Moschee angebracht.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Israel','Jerusalem','9053300','22380','Hare Meron(1208 m)','Jordan(320 km)','Schekel','Hebräisch und Arabisch',' Das moderne Hebräisch („Ivrit“) ist neben Arabisch die Amtssprache im Land. Es ist die einzige erfolgreiche Wiederbelebung einer Sprache, die fast nur von religiöser Bedeutung war.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Japan','Tokio','126226568','377835','Fuji(3776 m)','Shinano(367 km)','Yen','Japanisch','Entgegen der landläufigen Meinung waren die ersten japanischen Geishas tatsächlich Männer. Als sie in den 1730er Jahren auftauchten, nannten die Einheimischen diese männlichen Geishas Taikomochi oder Houkan. Die Rolle der männlichen Geisha ähnelte der eines Narren in der westlichen Kultur. Weibliche Geishas tauchten erst 20 Jahre später auf und übernahmen bis 1780 langsam den Beruf.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Jemen','Sanaa','29161922','528076','Dschabal an-Nabi Schuʿaib(3760 m)','','Jemen - Rial','Arabisch','Dieses Land ist eines der ältesten Zivilisationszentren der Erde. Diese Länder wurden vor etwa 1,65 Millionen Jahren von Menschen bewohnt. Wissenschaftler kennen mindestens 6 alte Königreiche, die in dieser Gegend entstanden sind.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Jordanien','Amman','10101694','89342','Dschabal Umm ad - Dami(1854 m)','Jordan(320 km)','Jordanischer Dinar','Arabisch','Petra ist eine der bekanntesten und beeindruckendsten Sehenswürdigkeiten in Jordanien. Die antike Felsenstadt wurde von den Nabatäern vor mehr als 2.000 Jahren erbaut und ist heute ein UNESCO-Weltkulturerbe.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kambodscha','Phnom Penh','16486542','181040','Phnom Aural(1813 m)','Mekong(4909 km)','Riel','Khmer','Kurz gefasst finden sich im Gebiet rund um den bekannten Tempel Angkor Wat viele weitere Tempel und Paläste des Khmer Reichs, deren älteste Bauwerke auf das Jahr 802 zurückgehen und hauptsächlich bis 1431 genutzt wurden. Die letzten Tempel wurden in der Epoche Jayavarmans VII. bis spätestens 1219 erbaut.Heute sind viele der Bauwerke nur noch als Ruinen zu besichtigen, Angkor Wat ist davon einer der am besten erhaltenen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kasachstan','Astana(Nursultan)','18513930','2724900','Khan Tengri(7010 m)','Irtysch(1700 km)','Tenge','Kasachisch und Russisch','Das größte Zelt der Welt befindet sich in Kasachstan – Unter einer riesigen Kuppel aus Polymermaterial befindet sich ein Einkaufs- und Unterhaltungszentrum mit Geschäften, Cafés, einem Privatstrand mit künstlicher Flut und einem Erholungspark. Die Fläche der von Norman Foster entworfenen Khan Shatyr-Struktur beträgt 127.000 Quadratmeter.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Katar','Doha','2832067','11437','Qurain Abu l - Baul(103 m)','','Katar - Riyal','Arabisch','Katar ist eine konstitutionelle Monarchie mit Arabisch als Amtssprache und Englisch als gemeinsamer zweiter Sprache. Dadurch hat sich das Land zu einem wichtigen Drehkreuz für die internationale Wirtschaft entwickelt, und viele multinationale Unternehmen haben sich im Land niedergelassen. Die Regierung hat auch stark in das Bildungswesen investiert, so dass in allen Schulen und Universitäten Englisch unterrichtet wird.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kirgisistan','Bischkek(Biškek)','6456900','199900','Dschengisch Tschokusu(7439 m)','Naryn(534 km)','Som','Kirgisisch, Russisch','Die Landesflagge zeigt eine gelbe Sonne mit 40 Strahlen auf rotem Hintergrund. In der Sonne sieht man einen roten Kreis mit sich kreuzenden Linien. Rot ist eine der traditionellen mongolischen Farben. Die 40 Strahlen stehen für die kirgisischen Stämme. Der rote Kreis symbolisiert das Dach einer Jurte, das Zelt der Nomaden in Zentralasien.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kuwait','Kuwait - Stadt','4207083','17818','Asch Schakaja(290 m)','','Kuwait - Dinar','Arabisch','Durch einen Erlass des Emirs im Jahr 2007 wurden den Bürgern des Landes Schulden in Höhe von insgesamt rund einer Milliarde Dollar erlassen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Laos','Vientiane','7169455','236800','Phu Bia(2820 m)','Mekong(4909 km)','Kip','Laotisch','Laos grenzt im Westen an Thailand, im Nordwesten an Myanmar (Burma), im Norden an China, im Osten an Vietnam und im Süden an Kambodscha. Es hat als einziger Binnenstaat Südostasiens keinen Zugang zum Meer und wird wesentlich durch das Bergland sowie den Mekong, den größten Fluss Asiens, geprägt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Libanon','Beirut','6855713','10452','Karnat as- Sauda(3088 m)','Litani(ca. 140 km)','Libanesisches Pfund','Arabisch','Die libanesische Zeder wird seit Jahrhunderten sehr hoch geschätzt, aber unkontrollierter Holzeinschlag hat dazu geführt, dass heute fast alle Bäume im Libanon verschwunden sind, und es gibt überhaupt keine Wälder (interessante Fakten über Bäume).')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Malaysia','Kuala Lumpur','32447385','330290','Kinabalu(4095 m)','Rajang(563 km)','Ringgit','Malaiisch',' Die grüne Stachelfrucht Durian ist eine sehr beliebte Frucht in Malaysia. In vielen Unterkünften und öffentlichen Orten ist sie jedoch strengstens verboten, da sie bestialisch stinkt, sobald du sie öffnest.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Malediven','Malé','530953','298','','','Rufiyaa','Dhivehi','Eine Familie auf den Malediven verbraucht in der Woche 20 bis 30 Kokosnüsse – nicht selten komplett: Das Öl dient als Speisefett und zum Beschichten der Holzboote, der milchige Saft wird zum Frühstück getrunken, die Kokosschalen dienen als Schüsseln und aus den Palmwedeln werden Flechtwaren hergestellt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Mongolei','Ulan Bator(Ulaanbaatar)','3225167','1564116','Nayramdal Uur(Hüyten, 4374 m)','Orchon(1124 km)','Mongolischer Tugrik','Mongolisch(Khalkha)','Das von Dschingis Khan gegründete Mongolenreich war das größte Reich in der Geschichte der Menschheit, verteilt über einen Kontinent. Diese Supermacht besetzte 22 % der Landoberfläche der Erde.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Myanmar','Naypyidaw','54045420','676578','Hkakabo Razi(5881 m)','Ayeyarwady(Irrawaddy, 2000 km)','Kyat','Burmesisch','Fischer am Inle-See in Myanmar sind berühmt dafür, auf einem Bein zu fischen. Die Einheimischen der Intha entwickelten über Jahrhunderte die ungewöhnliche Technik, um gleichzeitig Fischen und Rudern zu ermöglichen. Im Stehen können die Fischer durch das Schilf sehen, das knapp unter der Oberfläche im seichten Wasser des Sees liegt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Nepal','Kathmandu','28608710','147181','Mount Everest(8850 m)','Ghaghara(1080 km)','Nepalesische Rupie','Nepali','Hier liegt der Geburtsort von Buddha (Gautam Buddha). Zudem liegt hier der höchste Berg der Welt, nämlich der Mount Everest.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Nordkorea','Pjöngjang','25666161','122762','Paektusan(2744 m)','Yalu(790 km)','Won','Koreanisch, Russisch, Chinesisch','In Nordkorea dürfen Sie ihr Haar nicht tragen, wie Sie wollen. Das Magazin Time hat recherchiert, dass es 28 staatlich erlaubte Frisuren gibt - 14 für Frauen, 14 für Männer. Verheiratete Frauen etwa sollen Haare und Locken eher kurz halten, wer nicht verheiratet ist, darf das Haar länger lassen. Bei Männern hingegen muss es auf maximal fünf Zentimeter zurückgeschnitten werden. Alte Männer dürfen bis sieben Zentimeter wachsen lassen. ')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Oman','Maskat','5266535','309500','Dschabal Schams(3017 m)','','Rial Omani(OMR)','Arabisch','Oman ist ein Land, das nach dem System der absoluten Monarchie regiert wird und seit 1651 unabhängig ist, als es seine Freiheit vom portugiesischen Reich erlangte. Dieses Regierungssystem besteht seit Jahrhunderten und ist auch heute noch in Kraft. Oman ist ein Land mit einer reichen Geschichte und Kultur, und die Monarchie ist ein wichtiger Bestandteil davon. Der derzeitige Sultan von Oman, Qaboos bin Said Al Said, ist seit 1970 an der Macht.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Pakistan','Islamabad','216565318','796.095, mit Kaschmir: 880.254','K2(8611 m)','Indus(3180 km)','Pakistanische Rupie','Urdu und Englisch','Es gilt als beschämend Pakistanische Familien dürfen keinen Sohn haben, also haben Eltern, die nur Mädchen haben, das Recht, einige von ihnen als Jungen aufzuziehen. Solchen Kindern werden jungenhafte Namen gegeben, ihre Haare werden kurz geschnitten, sie tragen Jungenkleider. Kleidung und erhalten alle Freiheiten, die dem männlichen Teil der muslimischen Gesellschaft zur Verfügung stehen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Philippinen','Manila','108116615','343448','Mount Apo(2953 m)','Agusan(350 km)','Philippinischer Peso','Filipino, Englisch, Spanisch','Philippinen Es ist verboten, sich scheiden zu lassen und Schwangerschaften künstlich zu beenden. Außerdem gibt kommen viele gute Billard-Spieler, wie z.B. Efren Reyes aus den Philippinen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Russland','Moskau','144373535','17098200','Elbrus(5642 m)','Lena(4294 km)','Rubel','Russisch','In Russland existiert der kälteste bewohnte Ort der Welt: Oymyakon, ein kleines Dorf im Osten Russlands mit weniger als 500 Einwohnern. Am 6. Februar 1933 wurde dort eine Temperatur von -67,7 Grad Celsius gemessen. Kinder müssen zur Schule, wenn es wärmer als -55 Grad Celsius ist.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Saudi - Arabien','Riad','34268528','2149690','Dschabal Sauda(3133 Metern)','','Saudi - Riyal','Arabisch','Die Rub‘ al Khali, auch als “Leeres Quartier” bezeichnet, ist einer der trockensten Orte der Welt. Diese Wüste, die 1.000 Kilometer (620 Meilen) lang und 500 Kilometer (310 Meilen) breit ist, nimmt einen großen Teil Saudi-Arabiens ein.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Singapur','Singapur','5703569','719','Bukit Timah(162,5 m)','Sungei Seletar(?)','Singapur - Dollar','Tamil, Malaiisch, Chinesisch und Englisch','Singapur hat eine extrem hohe Bevölkerungsdichte (7891 Menschen pro km²) und zählt zu den am dicht besiedelten Städten weltweit. Die Einwohnerzahl Singapurs ist vor allem seit der Unabhängigkeitserklärung von Malaysia so rasant gewachsen. Heute leben etwa 5,5 Millionen Menschen in Singapur. Entsprechend ist die Innenstadt sehr stark bebaut und Singapurs Skyline wird von hohen Gebäuden dominiert.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Sri Lanka','Colombo / Sri Jayewardenepura Kotte','21803000','65610','Pidurutalagala(2534 m)','Mahaweli(335 km)','Sri - Lanka - Rupie','Singhala und Tamil','Das srilankische Team konnte 1996 die Cricket-Meisterschaft gewinnen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Südkorea','Seoul','51709098','99313','Hallasan(1950 m)','Nakdonggang(525 km)','Südkoreanischer Won','Koreanisch','Soul ist für seine zahlreichen verrückten Themencafes bekannt, welche sich an jeder Straßenecke finden lassen. Hier wird das Konzept großgeschrieben und vor allem eins: Durchgezogen von Anfang bis Ende! Es gibt z.B. Hallo Kitty Restaurants oder Barbie Diners. ')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Syrien','Damaskus','17070135','185180','Hermon(2814 m)','Euphrat(2736 km)','Syrisches Pfund','Arabisch','Syrien befindet sich in einem immer wieder aufflammenden Konflikt mit Israel, vor allem um die Golanhöhen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Tadschikistan','Duschanbe(Dušanbe)','9321018','143100','Pik Ismoil Somoni(7495 m)','Syrdarja(2212 km)','Somoni','Tadschikisch','Es gibt keine direkten Flüge nach Tadschikistan und leider auch keine direkten Züge. Mit dem Flugzeug kommt man nur in der Hauptstadt an. Aber von dort kann man problemlos Inlandsflüge buchen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Thailand','Bangkok','69625582','513115','Doi Inthanon(2565 m)','Chao Phraya(370 km)','Bhat','Thai','Eine Majestätsbeleidigung ist es ebenfalls, auf einen Geldschein zu treten, denn darauf ist der König abgebildet. Wenn ein Baht (die thailändische Währung) auf den Boden fällt, also lieber mit gebührendem Respekt wieder aufheben. Eine zu legere Kleiderordnung ist in Thailand auch nicht erlaubt: Auf das Verlassen des Heims ohne Unterwäsche sowie das Fahren von Moped oder Auto mit nacktem Oberkörper stehen laut Gesetz auch kleine Strafen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Osttimor','Dili','3500000','14918','Tatamailau(2963 m)','Lóis(80 km)','US - Dollar','Tetum und Portugiesisch','Die Insel Timor gehört zum östlichen Teil des malaiischen Archipels und zählt zu den Sundainseln. Es befindet sich auf dem äußeren Rand des sogenannten Bandabogens, einem Teil eines Ausläufers des pazifischen Feuerrings. Weil sich an dieser Stelle der nordwestliche Teil der Australischen Platte unter die Eurasische Platte schiebt, werden die Berge höher. So kann ein Berg zwischen 1 bis 1,6 Millimeter pro Jahr wachsen...')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Türkei','Ankara','83429615','783562','Ararat(5137 m)','Kizilirmak(1355 km)','Türkische Lira','Türkisch','Bis 1934 hatten die Türken keine Nachnamen. Kirschen wurde erstmals aus der Türkei nach Europa eingeführt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Turkmenistan','Aschgabat(Aşgabat)','5942089','488100','Aýrybaba(3139 m)','Amudarja(2743 km)','Manat','Turkmenisch','Ein Gaskrater mit dem Titel “The Gates of Hell” – es ist ein berühmtes Wahrzeichen von Turkmenistan. Seit 1971 wird dort Gas verbrannt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Usbekistan','Taschkent','33580650','447400','Hazrat Sulton(4643 m)','Amudarja(2743 km)','Sum','Usbekisch und Karakalpakisch','Die Blütezeit der Wissenschaft und Kultur dieser Region fiel auf das 14. Jahrhundert, als Tamerlan ihr Herrscher war. Er machte Samarkand, heute die drittgrößte Stadt des Landes, zur Hauptstadt seines Staates.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Vereinigte Arabische Emirate','Abu Dhabi','9770529','83600','Dschabal Yibir(1527 m)','','VAE - Dirham','Arabisch','Die Vereinigten Arabischen Emirate sind ein zu Hause für über 200 Nationalitäten. Die Auswanderer-Gemeinschaft ist bei weitem mehr als die Einheimischen selbst. Inder und Pakistaner sind die größten Gruppen von Auswanderer.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Vietnam','Hanoi','96462106','331210','Phan - xi - pang(Fansipan, 3143 m)','Roter Fluss(Yuan Jiang 1149 km)','Dong','Vietnamesisch','In Vietnam liegt die größte Höhle der Welt – die Son Doong Höhle. Sie ist eigentlich ein Höhlengang inmitten eines Höhlensystems mit etwa 150 einzelnen Höhlen. Son Doong ist mehr als 250 Meter hoch, 150 Meter breit und fünf Kilometer lang.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Volksrepublik China','Peking(Beijing)','1454000','9596960','Mount Everest(8848 m)','Jangtsekiang(6380 km)','Renminbi(Yuan)','Chinesisch(Mandarin)','Die älteste Sprache, die noch schriftlich verwendet wird, ist Chinesisch. In China sind die Webseiten von BBC News, Amnesty International und des Dalai Lama für die Bevölkerung gesperrt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Republik Zypern','Nikosia(Lefkoşa)','1120000','9251','Olympos(1952 m)','Pedias(circa 100 km)','Euro bzw Türkische Lyra','Griechisch und Türkisch','Ob archäologische Ausgrabungsstätte, historischer Sakralbau oder modernes Museum – Zypern ist ein internationaler Hotspot für Kulturinteressierte. Weil es kaum ein Urlauber schaffen wird, die 40 Klöster, Moscheen und Kathedralen der Insel zu besuchen, sollte man sich auf das Wesentliche konzentrieren. Wie wäre es beispielsweise mit einem Besuch der 9 Scheunendachkirchen im Tróodos-Gebirge, die es auf die Liste des UNESCO-Weltkulturerbes geschafft haben?')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Asia (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Taiwan','Taipeh','23580712','36179','Yushan(3952 m)','Zhuoshui(186, 6 km)','Neuer Taiwan-Dollar','Hochchinesisch, Taiwanisch','Taiwan beansprucht das Territorium der Republik Tiva, die Teil der Russischen Föderation ist. Alles aufgrund der Tatsache, dass Tuwa bis 1912 Teil Chinas war und dann unter das Protektorat des Russischen Reiches fiel. Nach einer Zeit der Unabhängigkeit wurde Tyva wieder Teil der UdSSR. Übrigens beansprucht Taiwan nicht nur das Territorium von Tuva, sondern auch das gesamte Festlandchina ')";
                cmmd.ExecuteNonQuery();
                #endregion

                #region North-America
                cmmd.CommandText = "CREATE TABLE North_America ([Id] INT NOT NULL PRIMARY KEY IDENTITY, [Name] NVARCHAR(100), [Capital] NVARCHAR(100), [Population] NVARCHAR(100),  [Area] NVARCHAR(100),  [Mountain] NVARCHAR(100), [River] NVARCHAR(100),  [Currency] NVARCHAR(100), [Languages] NVARCHAR(400), [Infos] NVARCHAR(4000))";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kanada','Ottawa','38753000','9984670','Mount Logan(5959 m)','Sankt - Lorenz - Strom(3058 km)','Kanadischer Dollar','Englisch, Französisch','Die Grenze zwischen Kanada und der USA ist die längste internationale unbewachte Grenze der Welt. Diese Grenze ist nicht nur die längste unbewachte Grenze der Welt, sondern auch die längste Grenze der Welt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Mexiko','Mexiko - Stadt(Ciudad de México)','133582231','1953162','Citlaltépetl(5636 m)','Río Bravo(3034 km)','Mexikanischer Peso','Spanisch und 62 weitere Sprachen, darunter Nahuatl','Nicht Ägypten, sondern Mexiko –  Die Pyramide von Cholula im Mexikanischen Bundesstaat Puebla ist die größte Pyramide der Welt. Mit einer Höhe von 66 Metern, einer Basis von 400 Metern und einem Gesamtvolumen von 4,5 Millionen Kubikmetern ist sie deutlich größer als die Pyramide von Giza, wenn auch deutlich weniger bekannt. Das liegt daran, dass man sie trotz ihrer beeindruckenden Größe geradezu übersehen kann: Die Pyramide von Cholula liegt verdeckt unter einem Berg. Blickfang ist daher eher die Kirche, die von den spanischen Kolonisatoren auf der Spitze der Pyramide errichtet wurde.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('USA','Washington, D.C.','337534633','9826675','Mount(Denali) McKinley in Alaska(6194 m)','Missouri(4130 km)','US - Dollar','Englisch','Im Jahr 1867 kauften die USA Alaska von Russland für lediglich 7,2 Millionen Dollar ab.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Belize','Belmopan','420703','22965','Doyle*s Delight  (1124 m)','Macal River (320 km)','Belize-Dollar','Englisch','Klar, das Great Barrier Reef kennen wir alle. Doch wusstet ihr, dass das zweitgrößte Riffsystem der Welt dem kleinen Land Belize zu Füßen liegt? Das Belize Barrier Reef kommt auf stolze 256 Kilometer Länge und bietet über 350 Fischarten ein Zuhause. Auch Wassersportlern bietet das zum UNESCO Welterbe erkorene Gebiet viele Möglichkeiten zum Windsurfen, Kitesurfen, Tauchen, Segeln und Co. ')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Guatemala','Guatemala - Stadt','18873834','108889','Tajumulco(4220 m)','Río Motagua(486 km)','Quetzal','Spanisch','Die guatemaltekische Währung Quetzal bekam ihren Name eines von den Mayas und Azteken verehrten Vogels. In der Vergangenheit verwendeten die Einheimischen die Federn dieses Vogels als Geld. Quetzal prangt auch auf der Flagge Guatemalas')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Honduras','Tegucigalpa','10263644','112492','Cerro Las Minas(2870 m)','Río Patuca(320 km)','Lempira','Spanisch','Die honduranische Flagge besteht aus drei horizontalen Streifen. Oben und unten siehst Du jeweils einen blauen Streifen, der die Lage des Landes zwischen dem Pazifischen Ozean und dem Karibischen Meer verdeutlicht. In der Mitte der Flagge befindet sich ein weißer Streifen, auf dem fünf blaue Sterne abgebildet sind. Die fünf Sterne repräsentieren die fünf Mitgliedsstaaten der Zentralamerikanischen Konföderation, zu der Honduras einst – zusammen mit Guatemala, El Salvador, Nicaragua und Costa Rica – gehört hat.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('El Salvador','San Salvador','6526295','21041','El Pital(2730 m)','Río Lempa(422 km)','Colón, aber auch US - Dollar','Spanisch','Der so genannte Fußballkrieg oder auch als 100-Stunden-Krieg bekannt, war ein kurzer Krieg zwischen El Salvador und Honduras im Jahr 1969. Der Name beruht auf der Tatsache, dass der Krieg begonnen wurde, nachdem El Salvador ein Fußballspiel gegen Honduras während eines WM-Qualifikationsspiels in Mexiko verloren hatte, was zu Unruhen und Todesopfern auf beiden Seiten führte.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Nicaragua','Managua','6817583','120254','Pico Mogotón(2438 m)','Río Coco(685 km)','Córdoba','Spanisch','Den Namen „Land der tausend Vulkane“ verdankt Nicaragua der Vielzahl an aktiven Vulkanen entlang der Pazifikküste. Die wohl bekanntesten sind Masaya, Momotombo und der Cerro Negro in der Nähe von León. Für alle, die keine Besteigung vornehmen möchten, gibt es Feuerberge, bei denen man quasi bis an den Kraterrand fahren und sich das Innere des Vulkans bei einem gemütlichen Spaziergang anschauen kann. Für alle Aktiven unter euch, gibt es auch größere Herausforderungen wie die Besteigung der Vulkane Concepión oder Maderas auf der Isla Ometepe. Den meisten Spaß bringt vermutlich der mit schwarzem Sand bedeckte Vulkan Cerro Negro. Er ist ein sehr beliebter Ort um zu Sandboarden. Dabei düst man mit einem Holzbrett, welches hier als eine Art Snowboard fungiert, den Vulkan herunter. Ein Adrenalin-Kick und so viel Spaß den ihr euch nicht entgehen lassen solltet.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Costa Rica','San José','5243355','51100','Cerro Chirripó(3820 m)','Río Térraba(160 km)','Colón','Spanisch','Im Jahr 2016 lieferten erneuerbare Energien in Costa Rica 98 Prozent der elektrischen Energie für das gesamte Land. Costa Rica will innerhalb der nächsten zwei Jahre sogar komplett CO2-Neutral werden.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Panama','Panama - Stadt(Panamá)','4493881','75517','Volcán Barú(3477 m)','Río Chucunaque(231 km)','Balboa und US - Dollar','Spanisch','Panama ist der einzige Ort auf der Welt, an dem man mit Hilfe des bloßen Auges sowohl den Pazifischen als auch den Atlantischen Ozean vom selben Ort aus sehen kann.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Antigua und Barbuda','Saint John*s','100545','442','Mount Obama (402 m)','','Ostkaribischer Dollar','Englisch','Der Name der Insel «Barbuda» erinnert an den Namen des Inselstaates «Barbados». Die Bedeutung beider Namen ist dieselbe, es ist nur so, dass Barbados auf Portugiesisch und nicht auf Spanisch benannt wurde.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Bahamas','Nassau','408636','13939','Mount Alvernia(63 m)','','Bahama - Dollar','Englisch','Die Bahamas beherbergen eines der längsten Unterwasserhöhlensysteme der Welt. Der Lucayan-Nationalpark auf Grand Bahama Island schützt das Höhlensystem zusammen mit erhaltenen Überresten der Lucayan-Indianer, der Ureinwohner der Inseln.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Barbados','Bridgetown','290044','430','Mount Hillaby(340 m)','','Barbados - Dollar','Englisch','1983 Die Armee von Barbados nahm an Operationen zur Besetzung Grenadas teil, und die Spannungen zwischen diesen karibischen Ländern sind bis heute spürbar .')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Dominica','Roseau','72359','751','Morne Diablotins(1447 m)','','Ostkaribischer Dollar','Englisch','Sie können für hunderttausend Dollar Staatsbürger von Dominica werden – die Regierung des Landes recht verkauft offiziell seine Pässe.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Dominikanische Republik','Santo Domingo','11197382','48671','Pico Duarte(3098 m)','Yaque del Norte(298 km)','Dominikanischer Peso','Spanisch',' Larimar oder auch Stein der Stefilia genannt, ist eine seltene blaue Variante des Silikatminerals Pektolith und ist nur in der Dominikanischen Republik zu finden. Romeo Santos Vater kommt hierher. Juan Luis Guerra auch, einer der bekannteren Bachata Künstlern mit 3 Grammys.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Grenada','St.George*s','113636','344','Mount Saint Catherine (840 m)','','Ostkaribischer Dollar','Englisch','Auf dem Meeresboden nahe der Küste von Grenada befindet sich ein einzigartiger Unterwasser-Skulpturenpark.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Haiti','Port - au - Prince','11805408','27750','Pic la Selle(2674 m)','Artibonite(240 km in Haiti)','Gourde','Französisch, Kreolisch','Hispaniola ist neben St. Martin eine von nur zwei Inseln in der Karibik, die von zwei souveränen Staaten gleichzeitig geteilt wird: Haiti und die Dominikanische Republik.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Jamaika','Kingston','2988137','10991','Blue Mountain Peak(2256 m)','Rio Minho(92 km)','Jamaika - Dollar','Englisch','Hier wurde der legendäre Musiker Bob Marley geboren, mit Graffiti und Plakaten die Sie hier überall sehen können.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kuba','Havanna(La Habana)','11378942','110860','Pico Turquino(1974 m)','Río Cauto(343 km)','Kubanischer Peso(CUP) und Konvertibler Peso(CUC)','Spanisch','An der kubanischen Küste befindet sich ein 3020 Quadratkilometer großes natürliches Riff. Damit ist es nach den Bahamas das zweitgrößte in der Karibik. Kuba hat einige einzigartige Wildtiere in seinen Riffen, die in diesem Teil der Welt heimisch sind, wie der kubanische Rifffisch.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('St.Kitts und Nevis','Basseterre','54329','269','Mount Liamuiga(1156 m)','','Ostkaribischer Dollar','Englisch','Es gibt weiße Strände und schwarze vulkanische Strände, je nachdem, auf welcher Seite der Insel Sie sich befinden. Auf der einen Seite der Insel gibt es schwarze Strände und auf der anderen weiße Strände.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('St.Lucia','Castries','187854','616','Mount Gimie(950 m)','Cul de Sac(?)','Ostkaribischer Dollar','Englisch','Die wunderschöne Insel St. Lucia ist die Heimat des St. Lucia Papageis, auch bekannt als Jacquot, der der Nationalvogel des Landes ist. Dieser lebhafte Vogel ist wissenschaftlich als Amazona Versicolor bekannt und leicht an seinem leuchtend grünen Gefieder, der gelben Stirn und den blauen Flügeln zu erkennen. Es handelt sich um eine Papageienart, die in der Karibik heimisch ist und nur auf der Insel St. Lucia vorkommt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('St.Vincent und die Grandinen','Kingstown','110861','389','Soufrière(1220 m)','','Ostkaribischer Dollar','Englisch','Die Flagge hat links einen blauen Streifen und rechts einen grünen. Der Streifen in der Mitte ist gelb und doppelt so breit wie die anderen beiden. Die Farben stehen für das Meer, die Sonne und die Natur. In der Mitte stehen drei grüne Rauten in der Form eines V für St. Vincent. Sie sind ein Symbol für den Spitznamen der Insel: Edelsteine der Antillen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO North_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Trinidad und Tobago','Port of Spain','1417289','5128','Cerro del Aripo(941 m)','','Trinidad und Tobago Dollar','Englisch','Trinidad und Tobago ist ein wichtiger Exporteur von Erdöl und Erdölprodukten, Düngemitteln, Stahlprodukten, Chemikalien, Zucker, Kaffee, Blumen, Kakao und Zitrusfrüchten. Das Land importiert auch eine Vielzahl von Waren, darunter Maschinen, Transportausrüstungen, Industrieerzeugnisse, lebende Tiere und Lebensmittel. Diese Vielfalt an Exporten und Importen hat dazu beigetragen, Trinidad und Tobago zu einem wichtigen Akteur in der Weltwirtschaft zu machen, wobei die Exporte und Importe erheblich zum Wirtschaftswachstum des Landes beitragen.')";
                cmmd.ExecuteNonQuery();
                #endregion

                #region South-America
                cmmd.CommandText = "CREATE TABLE South_America ([Id] INT NOT NULL PRIMARY KEY IDENTITY, [Name] NVARCHAR(100), [Capital] NVARCHAR(100), [Population] NVARCHAR(100), [Area] NVARCHAR(100), [Mountain] NVARCHAR(100), [River] NVARCHAR(100), [Currency] NVARCHAR(100), [Languages] NVARCHAR(400), [Infos] NVARCHAR(4000))";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Argentinien','Buenos Aires','44938712','2780403','Aconcagua(6962 m)','Río Colorado(1114 km)','Peso','Spanisch','Tango! 1913 war Argentinien gemessen am pro-Kopf-Einkommen die zehntreichste Nation der Welt. Jetzt steht es nur noch an 54. Stelle. Die längste Straße der Welt verbindet Alaska mit dem Süden Argentiniens. Sie besitzt eine Länge von circa 30.000 Kilometern und durchquert 17 Staaten, sechs Zeit- und vier Klimazonen (Panamericana)')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Bolivien','Sucre(Regierungssitz: La Paz)','11513100','1098581','Sajama(6542 m)','Río Mamoré(1170 km)','Boliviano','Spanisch,  außerdem 36 indigene Sprachen, darunter Quechua, Aymara und Guaraní','Bis 2003 betrug die durchschnittliche Größe einer Frau in Bolivien nur 142,2 Zentimeter, was klein genug ist, um als Zwerg betrachtet zu werden.Salar de Uyuni - ist mit 10.582 Quadratkilometern die größte Salzwüste der Welt. Sie liegt im Südwesten Boliviens, ist fast so groß wie Jamaika, enthält mehr als zehn Milliarden Tonnen Salz und weist keine Höhenunterschiede von mehr als einem Meter auf.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Brasilien','Brasilia','212559417','8547404','Pico da Nablina(2994 m)','Amazonas(6448 km)','Real','Portugiesisch','Gisele Bündchen (Model) und Anitta (Sängerin) kommen aus Brasilien.In Brasilien lebt die größte katholische Bevölkerung. Die Mittellinie des Fußballstadions im brasilianischen Macapá liegt exakt auf der Äquatorlinie, sodass sich die konkurrierenden Teams bei einem Spiel jeweils immer auf unterschiedlichen Hemisphären befinden. ')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Chile','Santiago de Chile','18952038','756096','Ojos del Salado(6893 m)','Río Loa(443 km)','Peso','Spanisch','Chile ist die Heimat der Osterinsel. Die Insel ist vor allem für ihre fast 1.000 monolithischen menschlichen Figuren, die sogenannten „Moai“, berühmt. Sie wurden vom Volk der Rapa Nui zwischen 1250 und 1500 gemeißelt. Mit einer Länge von 4.270 Kilometern ist Chile von Norden nach Süden gemessen das längste Land der Welt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Ecuador','Quito','17373662','256370','Chimborazo(6267 m)','Río Napo(950 km)','US - Dollar','Spanisch','Ecuador war 2008 das erste Land, das die Rechte der Natur offiziell anerkannte. Anstatt die Natur als Eigentum zu behandeln, erkennt Ecuador an, dass die Natur verfassungsmäßige Rechte hat und das Recht hat, zu existieren, fortzubestehen, ihre Lebenskreisläufe zu erhalten und zu regenerieren.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Guyana','Georgetown','782766','214969','Roraima - Tepui(2810 m)','Essequibo(1010 km)','Guyana - Dollar','Englisch','Guyana besteht aus überwiegend Regenwald. Es ist tatsächlich so, dass 75% des Landes Wald ist. Intakter Wald und nur 2,5% des Landes sind kultiviert.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kolumbien','Bogotá','50339443','1141748','Pico Cristóbal Colón und Pico Simón Bolívar(beide 5775 m)','Río Magdalena(1540 km)','Peso','Spanisch','Shakira - ein Weltstar. Karol G. kommt auch hierher.In Kolumbien leben rosa Delfine. Es ist eines der einzigartigen Tiere, die nur in diesem Land zu finden sind.Anfang der 1980er Jahre war Pablo Escobar für 80 Prozent des weltweit produzierten Kokains verantwortlich.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Paraguay','Asunción','7044636','406752','Cerro Peró(842 m)','Río Paraguay(2549 km)','Guaraní','Spanisch, Guaraní','Der Krieg Paraguays mit Brasilien, Uruguay und Argentinien in der zweiten Hälfte des 19. Jahrhunderts wurde zu einer wahren Katastrophe für den Staat – das Land verlor die Hälfte seiner Territorien und seine männliche Bevölkerung ist um das Neunfache zurückgegangen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Peru','Lima','32510453','1285216','Nevado Huascarán(6768 m)','Río Ucayali(1771 km)','Neuer Sol','Spanisch','Der Titicacasee in Bolivien und Peru ist der höchste schiffbare See der Welt. Er liegt auf einer Höhe von 3.812 Metern. In der Atacama-Wüste in Peru fällt weniger Niederschlag als in jeder anderen Wüste der Welt, einschließlich der Polarwüsten. Der durchschnittliche Niederschlag beträgt dort an manchen Stellen lediglich etwa einen Millimeter im Jahr.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Suriname','Paramaribo','581372','163265','Julianatop(1280 m)','Suriname(480 km)','Suriname - Dollar','Niederländisch','Der Brownsburg Park in Suriname ist eines der ausgedehntesten Gebiete des unberührten Regenwaldes der Welt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Uruguay','Montevideo','3461734','176215','Cerro Catedral(514 m)','Río Negro(750 km)','Peso','Spanisch','Die einzigen olympischen Goldmedaillen, die Uruguay je gewonnen hat, sind für Fußball bei den Olympischen Spielen 1924 und 1928.Uruguay ist das einzige Land, dessen Name in den ersten fünf Buchstaben dreimal den gleichen Buchstaben hat.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO South_America (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Venezuela','Caracas','28515829','912050','Pico Bolívar(4981 m)','Orinoco(2140 km)','Bolívar','Spanisch, 31 Regionalsprachen','Shannon de Lima - venezoleanisches Model, das früher mit Marc Anthony verheiratet war. Der größte Wasserfall der Welt ist Salto Ángel in Venezuela mit einer Fallhöhe von 979 Metern.')";
                cmmd.ExecuteNonQuery();
                #endregion

                #region Europe
                cmmd.CommandText = "CREATE TABLE Europe ([Id] INT NOT NULL PRIMARY KEY IDENTITY, [Name] NVARCHAR(100), [Capital] NVARCHAR(100), [Population] NVARCHAR(100),  [Area] NVARCHAR(100), [Mountain] NVARCHAR(100), [River] NVARCHAR(100), [Currency] NVARCHAR(100), [Languages] NVARCHAR(400), [Infos] NVARCHAR(4000))";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Albanien','Tirana','2876246','28748','Korab(2764 m)','Drin(285 km)','Lek','Albanisch','Ahmet Zogu, Präsident Albaniens in der ersten Hälfte des 20. Jahrhunderts, überlebte mehr als 50 Attentatsversuche in 9 Jahren. Einmal eröffnete er als Reaktion darauf selbst das Feuer auf die Verschwörer, die ihn angriffen, was ihn anscheinend rettete.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Andorra','Andorra la Vella','77142','468','Coma Pedrosa(2942 m)','Valira(44 km)','Euro','Katalanisch','Die hoch in den Pyrenäen gelegene Hauptstadt Andorras, Andorra la Vella, ist die höchstgelegene Hauptstadt Europas und befindet sich auf einer beeindruckenden Höhe von 3.356 Fuß über dem Meeresspiegel. Die malerische Stadt ist ein beliebtes Reiseziel und bietet Besuchern eine einzigartige Mischung aus spanischer und französischer Kultur sowie eine Vielzahl von Outdoor-Aktivitäten wie Skifahren, Wandern und Mountainbiking. Mit seinem atemberaubenden Bergpanorama und seiner lebendigen Kultur ist Andorra la Vella ein Muss für jeden Reisenden.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Weißrussland','Minsk','9466856','207595','Dsjarschynskaja Hara(345 m)','Dnepr(2145 km)','Weißrussischer Rubel','Weißrussisch und Russisch','Yakub Kolas und Yanka Kupala sind die bekanntesten Dichter von Belarus')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Belgien','Brüssel(Bruxelles)','11484055','30520','Botrange(694 m)','Maas(874 km)','Euro','Französisch, Flämisch(Niederländisch), Deutsch','1840 wurde das erste Saxophon in Belgien erfunden, ja von Adolphe Sax. Pommes Frites gelten als das Schlüsselgericht der Belgier.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Bosnien','Sarajevo','3301000','51197','Maglić(2386 m)','Drina(346 km)','Konvertible Mark','Bosnisch, Serbisch und Kroatisch','Viele Gebäude im historischen Zentrum von Sarajevo, der Hauptstadt von Bosnien und Herzegowina, wurden in jenen fernen Zeiten erbaut, als dieses Land den Osmanen gehörte Empire.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Bulgarien','Sofia','6975761','110994','Musala (2925 m)','Donau(2857 km)','Lew','Bulgarisch','In Bulgarien werden Namenstage mit Geburtstagen gleichgesetzt, sie werden dort in besonderem Rahmen gefeiert. Bulgarien produziert die Hälfte des weltweiten Rosenöls, das aktiv zur Herstellung von Parfüms verwendet wird.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Dänemark','Kopenhagen','5818553','43094','Møllehøj(170, 86 m)','Gudenå(176 km)','Dänische Krone','Dänisch','In den meisten Ländern gibt es spezielle Traditionen für den Silvesterabend. Während man in vielen Ländern darauf schwört, in der Silvesternacht rote Unterwäsche zu tragen, weil das Glück für das neue Jahr bringen soll, veranstalten wir Bleigiessen, deuten unser Zukunft und wünschen uns dann einen guten Rutsch. Die Dänen hingegen springen kurz vor Mitternacht schnell von einem Stuhl oder Sessel. Kaum zu glauben aber auch das soll Glück bringen. Das Feiern an sich beginnt schon sehr zeitig mit der Ansprache der Dänischen Königin.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Deutschland','Berlin','83132799','357376','Zugspitze(2962 m)','Rhein(1232, 7 km)','Euro','Deutsch','Die Germanen nannte man „Theodiscus“, was so viel bedeutet wie zugehörig zum Volk mit germanischer Sprache. Das Wort Theodiscus wurde dann nach und nach zu diutsch, teutsch und letztendlich zu deutsch. Deutschland heißt also so, weil die Menschen „deutsch“ gesprochen haben.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Estland','Tallinn','1331824','45227','Suur Munamägi(318 m)','Võhandu(162 km)','Euro','Estnisch','Während Tallinn die offizielle Hauptstadt von Estland ist, gibt es einige andere Hauptstädte die das Land selber ernannt hat: Tartu ist die langjährige Kulturhauptstadt, Pärnu die Hauptstadt des Sommers, Kuressaare die Hauptstadt der Hochzeiten, Jõgeva die Winterhauptstadt und Türi die Frühlingshauptstadt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Finnland','Helsinki','5520314','338144','Haltitunturi(1324 m)','Kemijoki(550 km)','Euro','Finnisch, Schwedisch','Finnen haben viele kuriose Wettkampfdisziplinen. Beispielsweise gibt es den Handyweitwurf, Moorfußball, Frauentragen, Gummistiefelwerfen, Sauna anheizen, Luftgitarre spielen und Heavy-Metal-Knitting als Wettkämpfe. ')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Frankreich','Paris','67059887','643801','Mont Blanc(4810 m)','Loire(1004 km)','Euro','Französisch','Berücksichtigt man die französischen Überseeregionen im Pazifik, dem Atlantik und dem Indischen Ozean, dann ist Frankreich das Land mit den meisten Zeitzonen. Insgesamt sind es zwölf verschiedene.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Griechenland','Athen','10716322','131957','Olymp(2918 m)','Aliakmonas(297 km)','Euro','Griechisch','Jährlich gibt es in Griechenland im Durchschnitt etwa 3.000 Sonnenstunden. Damit gilt Griechenland als eines der sonnenreichsten Länder in Europa. Griechenland hat mehrere warme Tage, an denen sowohl Einwohner als auch Besucher bis in den November hinein bequem baden können. Das Land hält auch den Weltrekord für die meisten olympischen Medaillen im Verhältnis zur Bevölkerung des Landes.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('UK','London','66834405','242910','Ben Nevis(1343 m)','Severn(354 km)','Britisches Pfund Sterling','Englisch, regional auch Kornisch, Irisch, Schottisch - Gälisch, Walisisch','1724 wurde die Schottin Maggie Dickson zum Tod durch Galgen verurteilt. Als sie gehängt wurde und in einem Sarg abtransportiert wurde, stellte sich heraus, dass sie überlebt hatte. Ein Gericht entschied, dass die Strafe offiziell vollzogen wurde, sodass sie nicht weiter bestraft werden konnte. Sie lebte noch über 40 Jahre und erhielt den Spitznamen Half-Hangit Maggie.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Irland','Dublin','5007069','70273','Carrantuohill(1041 m)','Shannon(386 km)','Euro','Irisch, Englisch','Entgegen der landläufigen Meinung und obwohl St. Patrick der Schutzpatron Irlands war, stammte er nicht aus Irland. Er wurde um 386 n. Chr. In Wales geboren, von den Iren gefangen genommen und in Irland als Sklave verkauft. Er arbeitete als Hirte im Westen Irlands. Später kehrte er als Missionar nach Irland zurück und half, das Christentum zu verbreiten.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Island','Reykjavik','347359','103000','Hvannadalshnúkur(2110 m)','Thjorsa(Isländisch: Þjórsá, 230 km)','Isländische Krone','Isländisch','Aufgrund der geringen Gesamtbevölkerung Islands hat keine Stadt des Landes mehr als 1 Million Einwohner. Die bevölkerungsreichste bzw. größte Stadt Islands ist die Hauptstadt Reykjavik , während die anderen Städte des Landes alle weniger als 100 Tausend Einwohner haben. Die am wenigsten bevölkerte Stadt des Landes heißt Borgames, während die bevölkerungsreichste Stadt Reykjavik ( 128.000 ) ist.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Italien','Rom','60297396','301336','Mont Blanc de Courmayeur(4748 m)','Po(652 km)','Euro','Italienisch','In Italien gibt es zwei weitere unabhängige Staaten. Der erste ist die Vatikanstadt, die nur 0,17 Quadratkilometer groß ist, während der zweite die Republik San Marino mit nur 23 Quadratkilometern ist.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kosovo','Pristina','1824000','10877','Gjeravica(2656 m)','Weißer Drin(285 km)','Euro','Albanisch, Serbisch, Türkisch, Bosnisch, Romanes','Das Kosovo ist ein gebirgiges Land, dessen höchster Gipfel, Gjeravica, eine beeindruckende Höhe von 8.714 Fuß über dem Meeresspiegel erreicht. Es gibt zwei große Ebenen sowie eine Reihe von Flüssen und Seen, darunter der Weiße Drin, der Schwarze Drin, der Ibar und der Gazivoda-See. Das Gelände des Kosovo ist unglaublich vielfältig und bietet mit seinen Bergen, Ebenen, Flüssen und Seen eine einzigartige und wunderschöne Landschaft.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Kroatien','Zagreb','4067500','56594','Dinara(1831 m)','Save(945 km)','Kuna','Kroatisch','Der Dalmatiner kommt aus Kroatien. Diese freundliche, schwarz gepunktete Hunderasse, die durch den Disney-Zeichentrick-Film berühmt wurde. Benannt ist dieser Hund nach seiner Heimat an der dalmatinischen Küste. ')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Lettland','Riga','1912789','64589','Gaising(311 m)','Gauja(452 km)','Euro','Lettisch','Lettland hat laut einer weltweiten Umfrage den zweiten Platz im globalen Umweltleistungsindex belegt. Diese beeindruckende Platzierung ist ein Beweis für das Engagement Lettlands für Umweltschutz und Nachhaltigkeit. Das Land hat eine Reihe von Initiativen ergriffen, um seinen CO2-Fußabdruck zu verringern, z. B. durch Investitionen in erneuerbare Energiequellen, die Förderung der Energieeffizienz und die Einführung umweltfreundlicher Transportlösungen. Lettland hat auch Maßnahmen zum Schutz der biologischen Vielfalt ergriffen, wie die Einrichtung von Schutzgebieten und die Einführung einer nachhaltigen Forstwirtschaft. Diese Bemühungen haben sich ausgezahlt, denn Lettland hat ein hohes Maß an Umweltverträglichkeit erreicht und gehört nun zu den weltweit führenden Ländern in diesem Bereich.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Liechtenstein','Vaduz','38019','160','Grauspitz(2599 m)','Rhein(1232, 7 km)','Schweizer Franken','Deutsch','Das Haus Liechtenstein zählt zu den ältesten österreichischen Adelsfamilien, den sogenannten Apostelgeschlechtern. Um 1136 wird mit Hugo von Liechtenstein erstmals ein Träger dieses Namens erwähnt. Das Haus zählt über hundert Mitglieder, von denen nur ein Teil im Fürstentum Liechtenstein lebt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Litauen','Vilnius','2786844','65301','Aukštojas(293 m)','Memel  937 km)','Euro','Litauisch','Schwarzbrot In Litauen wird es noch immer nach traditionellen Rezepten zubereitet und bis zu 3-4 Wochen gelagert. Es gilt geschmacklich als eines der besten der Welt.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Luxemburg','Luxemburg','645397','2586','Kneiff(560 m)','Sauer(173 km)','Euro','Luxemburgisch, Deutsch, Französisch','Die ersten Menschen auf dem Gebiet des modernen Luxemburg erschienen vor etwa 37.000 Jahren, wie archäologische Funde belegen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Malta','Valletta','502653','316','Ta*Dmejrek (253 m)','','Euro','Maltesisch und Englisch','Laut einer Studie des südafrikanischen Wohlstandsforschungsinstituts New World Wealth (NWW) aus dem Jahr 2019 geht hervor, dass Malta (nach Australien) das zweitsicherste Land der Welt ist. Betrachtet man nur Europa, gilt Malta sogar als das sicherste.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Rep. Moldau','Chișinău(Kischinau)','2657637','33800','Dealul Bălănești(430 m)','Pruth(953 km)','Moldau - Leu','Rumänisch','Moldawien ist ein Land mit einer reichen Weinbaugeschichte, und es ist keine Überraschung, dass es mit Platz 7 zu den größten Weinexporteuren der Welt gehört. Das Land beherbergt den größten unterirdischen Weinkeller der Welt, Cricova, ein 120 km langes Netz von Galerien und Tunneln, das mit über 1,25 Millionen Weinflaschen gefüllt ist. Dieser einzigartige Keller ist ein Muss für jeden Weinliebhaber und zeugt von der langjährigen Weinbautradition Moldawiens.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Monaco','Monaco - Ville','38964','2,02','Liegt an der Grenze: Mont Agel(1148 m)','','Euro','Französisch und Monegassisch','Bevölkerungsdichte in Monaco – die höchste der Welt, hier leben etwa 30.000 Menschen, das heißt etwa 15.000 pro Quadrat Kilometer. Außerdem sind mehr als 80 Prozent der Bevölkerung — Ausländer.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Montenegro','Podgorica','622137','13812','Bobotov Kuk(2522 m)','Tara(140 km)','Euro','Montenegrinisch','In der montenegrinischen Stadt Kotor gibt es die engste Gasse in der Welt, etwa einen halben Meter breit. Und in der Hauptstadt Podgorica gibt es die kürzeste Straße mit einem einzigen Haus und nur dreißig Metern Länge.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Niederlande','Amsterdam','17332850','41526','Mount Scenery(877 m) auf Saba','Maas(874 km)','Euro','Niederländisch','Den Haag liegt in der Region Holland in den Niederlanden. Die Stadt beherbergt vier internationale Gerichtshöfe, was ihr den Ruf als Welthauptstadt des Rechts eingebracht hat. Die vier Gerichte sind der Ständige Schiedsgerichtshof, der Internationale Gerichtshof, der Internationale Strafgerichtshof für das ehemalige Jugoslawien und der Internationale Strafgerichtshof. Den Haag beherbergt neben New York, Nairobi, Genf und Wien auch die Vereinten Nationen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Nordmazedonien','Skopje','1836713','25713','Korab(2764 m)','Vardar(388 km)','Denar','Mazedonisch und Albanisch, außerdem Türkisch, Romani, Serbisch und Aromunisch','Das Schießen mit zwei Pistolen mit zwei Händen wurde von den mazedonischen Geheimdiensten erfunden, weshalb es „mazedonisches Schießen“ genannt wird.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Norwegen','Oslo','5347896','323759','Galdhøpiggen(2469 m)','Glomma(601 km)','Norwegische Krone','Norwegisch','Ein Fjord ist ein langgestreckter, U-förmiger Meeres- oder Seeabfluss, der an drei Seiten von steilem Land und an einer Seite von einer flachen Öffnung umgeben ist und von riesigen Gletschern gebildet wird. Andere Dinge, mit denen sich das Land rühmt, sind Eisbären, Rentiere, Fisch, Walfleisch, Waffeln und die Wikinger.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Österreich','Wien','8877067','83879','Großglockner(3798 m)','Donau(2857 km)','Euro','Deutsch, Slowenisch(regional), Kroatisch(regional), Ungarisch','Der Name Österreich kommt vom altdeutschen Wort “Ostarrichi” und bedeutet übersetzt „östliches Land“. Erstmals wurde dieser Name 996 v. Chr. erwähnt. Die österreichische Flagge ist die älteste Staatsflagge der Welt, die ihren Ursprung im Jahr 1191 hat.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Polen','Warschau','37970874','312685','Rysy(2503 m)','Weichsel(1022 km)','Zloty','Polnisch','Johannes Paul II., der 264. Papst, war ursprünglich Pole, und sein Name war Karol Wojtyla. Die Polen glauben an die Legende, dass der Name der polnischen Hauptstadt Warschau entstammt aus der Verschmelzung der Namen des Fischers Warsha und der Meerjungfrau Sava, die er heiratete.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Portugal','Lissabon','10269417','92345','Ponta do Pico auf den Azoren(2351 m)','Douro(320 km)','Euro','Portugiesisch','Der portugiesische Seefahrer Pedro Álvares Cabral gilt als der Entdecker von Brasilien um 1500, dabei war er eigentlich auf dem Weg nach Indien. Portugal ist seit der Nelkenrevolution (Militärputsch) eine semipräsidentielle Republik mit einem Regierungschef und einem Staatsoberhaupt, mit der Erlaubnis zur Ernennung des Premierministers und anderer Regierungsmitglieder.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Rumänien','Bukarest(Bucureşti)','19356544','238391','Moldoveanu(2544 m)','Donau(2857 km)','Leu','Rumänisch','Der archetypische Vampir Graf Dracula, erfunden von Bram Stoker, wurde von dem gnadenlosen rumänischen General Vlad Tepes inspiriert, der auch als Vlad der Pfähler bekannt ist, denn eine seiner bevorzugten Arten, Menschen zu bestrafen, war das Pfählen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Russland','Moskau','144373535','17098200','Elbrus(5642 m)','Lena(4294 km)','Rubel','Russisch','Der russische Wissenschaftler Anatoli Bugorski hat 1978 versehentlich seinen Kopf während einer Überprüfung in einen laufenden Teilchenbeschleuniger gehalten. Die Ärzte gingen davon aus, dass er wenige Tage später sterben sollte, was jedoch nicht passierte. Lediglich eine Gesichtshälfte wurde gelähmt, sein linkes Ohr ist taub und er erleidet regelmäßig Epilepsieanfälle.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('San Marino','San Marino','33860','61','Monte Titano(739 m)','Marano(?)','Euro','Italienisch','Bis 1987 hatte San Marino keinen einzigen eigenen Fernsehsender, da das mit Italien geschlossene Abkommen der Republik untersagte, ein eigenes Fernsehzentrum oder einen eigenen Radiosender zu unterhalten.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Schweden','Stockholm','10285453','449964','Kebnekaise(2097 m)','Klarälven(720 km)','Schwedische Krone','Schwedisch','Schweden hat ca. 221.800 Inseln und ist damit das inselreichste Land der Welt')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Schweiz','Bern','8574832','41285','Dufourspitze(4634 m)','Rhein(1232, 7 km)','Schweizer Franken','Deutsch, Französisch, Italienisch, Rätoromanisch','Die Schweiz ist teuer. Das ist ein sehr altbekannter Fakt. Aber er muss trotzdem erwähnt werden. Die Schweiz ist eines der teuersten Länder der Welt. Es ist hilfreich, dass du dir dessen bewusst bist, bevor du hierher kommst. Das bedeutet, dass du bei Starbucks, im Restaurant, bei Tagesausflügen und beim Einkaufen mehr Geld ausgeben wirst, als du es vielleicht gewohnt bist. ')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Serbien','Belgrad','6944975','88361','Midžor(2169 m)','Donau(2857 km)','Dinar','Serbisch','Serbien ist einer der größten Himbeerexporteure der Welt. Darüber hinaus war Serbien der größte Exporteur von Pflaumen und Pflaumen, und in letzter Zeit wächst sein Potenzial für den Export von Kirschen.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Slowakei','Bratislava(Pressburg)','5454073','49034','Gerlsdorfer Spitze(Gerlachovský štít, 2655 m)','Waag(403 km)','Euro','Slowakisch','Hier wurde der erste Massenfallschirm erfunden Anfang des 20. Jahrhunderts. Um die Zuverlässigkeit seines Produkts zu beweisen, sprang der Erfinder persönlich mit seinem Fallschirm vom Dach eines hohen Gebäudes.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Slowenien','Ljubljana','2087946','20273','Triglav(2864 m)','Save(945 km)','Euro','Slowenisch','Der berühmteste slowenische Architekt ist Jože Plečnik, der viele Gebäude in Ljubljana entworfen hat.Slowenien hat eine der ältesten Weinregionen der Welt, die Region Brda.')";
                cmmd.ExecuteNonQuery();
                cmmd.CommandText = "INSERT INTO Europe (Name, Capital, Population, Area,Mountain, River, Currency, Languages, Infos) VALUES ('Spanien','Madrid','47076781','504645','Pico del Teide auf Teneriffa(3718 m)','Ebro(910 km)','Euro','Spanisch, regional auch Katalanisch, Galizisch','Spanien wurde einst als „das Reich, in dem die Sonne nie untergeht“ bezeichnet.Diese Bezeichnung ist auf die vielen Gebiete zurückzuführen, die Spanien zwischen dem späten 15. und der Mitte des 20. Jahrhunderts erobert hat. Die heutigen Länder in Europa, Amerika, Afrika, Ozeanien und sogar der philippinische Archipel waren einst spanische Territorien. Aufgrund der geografischen Ausdehnung des Landes war zu jeder Zeit mindestens ein Teil des Reiches im Tageslicht. Kaiser Karl V.')";
                cmmd.ExecuteNonQuery();
